import React, { useState } from "react";
import axios from "axios";

function App() {
  const [weight, setWeight] = useState("");
  const [height, setHeight] = useState("");
  const [encryptedData, setEncryptedData] = useState(null);
  const [decryptedBmi, setDecryptedBmi] = useState(null);
  const [errorMessage, setErrorMessage] = useState("");

  const handleEncryptBMI = async () => {
    setErrorMessage(""); // Reset error message
    setDecryptedBmi(null); // Reset previous decryption

    if (!weight || !height) {
      setErrorMessage("Please enter both weight and height.");
      return;
    }

    try {
      const response = await axios.post(
        "http://127.0.0.1:5000/api/encrypt_bmi",
        {
          weight: parseFloat(weight),
          height: parseFloat(height),
        }
      );

      setEncryptedData(response.data);
    } catch (error) {
      setErrorMessage("BMI Encryption error: " + error.message);
      console.error(error);
    }
  };

  const handleDecryptBMI = async () => {
    setErrorMessage(""); // Reset error message

    if (!encryptedData) {
      setErrorMessage("No encrypted BMI data found! Please encrypt first.");
      return;
    }

    try {
      const response = await axios.post(
        "http://127.0.0.1:5000/api/decrypt_bmi",
        {
          encrypted_bmi: encryptedData.encrypted_bmi,
        }
      );

      setDecryptedBmi(response.data.decrypted_bmi);
    } catch (error) {
      setErrorMessage("BMI Decryption error: " + error.message);
      console.error(error);
    }
  };

  return (
    <div className="App" style={{ textAlign: "center", marginTop: "50px" }}>
      <h1>Homomorphic Encryption BMI Calculation</h1>

      <input
        type="number"
        placeholder="Enter weight (kg)"
        value={weight}
        onChange={(e) => setWeight(e.target.value)}
        style={{ margin: "10px", padding: "10px" }}
      />
      <input
        type="number"
        placeholder="Enter height (m)"
        value={height}
        onChange={(e) => setHeight(e.target.value)}
        style={{ margin: "10px", padding: "10px" }}
      />

      <button
        onClick={handleEncryptBMI}
        style={{ margin: "10px", padding: "10px" }}
      >
        Encrypt and Compute BMI
      </button>
      <button
        onClick={handleDecryptBMI}
        style={{ margin: "10px", padding: "10px" }}
      >
        Decrypt BMI
      </button>

      {errorMessage && <p style={{ color: "red" }}>{errorMessage}</p>}

      {encryptedData && (
        <div style={{ marginTop: "20px" }}>
          <h2>Encrypted Data</h2>
          <p>
            <strong>Encrypted Weight:</strong> {encryptedData.encrypted_weight}
          </p>
          <p>
            <strong>Encrypted Height:</strong> {encryptedData.encrypted_height}
          </p>
          <p>
            <strong>Encrypted BMI:</strong> {encryptedData.encrypted_bmi}
          </p>
        </div>
      )}

      {decryptedBmi !== null && (
        <div style={{ marginTop: "20px", color: "green" }}>
          <h2>Decrypted BMI</h2>
          <p>
            <strong>BMI:</strong> {decryptedBmi}
          </p>
        </div>
      )}
    </div>
  );
}

export default App;
